INSERT INTO `services` (`id`, `name`, `price`, `sale`, `time`, `detail`, `images`, `id_type`) VALUES
(1, 'Uốn phồng tiêu chuẩn', 72000, 0, '00:35:00', '<p>V&Igrave; SAO UỐN T&Oacute;C L&Agrave; XU HƯỚNG CỦA NAM GIỚI HIỆN ĐẠI?</p>\r\n<p>Uốn t&oacute;c đang l&agrave; xu hướng được nam giới ưa chuộng bởi qu&aacute; nhiều lợi &iacute;ch m&agrave; n&oacute; mang lại. C&aacute;c khuyết điểm t&oacute;c m&agrave; nam giới thường gặp phải đều được giải quyết nhanh gọn bằng kỹ thuật n&agrave;y.</p>\r\n<p>Nếu anh sở hữu một m&aacute;i t&oacute;c thưa, mỏng, uốn t&oacute;c sẽ gi&uacute;p m&aacute;i t&oacute;c trở n&ecirc;n bồng bềnh, tạo hiệu ứng tr&ocirc;ng d&agrave;y hơn. Ngược lại, những m&aacute;i t&oacute;c d&agrave;y, kh&ocirc; xơ sẽ trở n&ecirc;n mềm mại, nhẹ nh&agrave;ng hơn rất nhiều khi được uốn.</p>\r\n<p>Dạo một v&ograve;ng quanh showbiz, đ&acirc;u đ&acirc;u cũng l&agrave; những mẫu t&oacute;c uốn tuyệt đẹp. Sơn T&ugrave;ng M-TP, Isaac, Soobin Ho&agrave;ng Sơn, Justatee,... tất cả đều kh&ocirc;ng thể đứng ngo&agrave;i cơn sốt uốn t&oacute;c.</p>\r\n<p>H&agrave;ng ng&agrave;n ch&agrave;ng trai đ&atilde; tự tin thay đổi m&igrave;nh với xu hướng t&oacute;c uốn rồi, anh cũng rất n&ecirc;n uốn t&oacute;c một lần trong đời để cảm nhận hết vẻ ho&agrave;n hảo m&agrave; m&aacute;i t&oacute;c uốn mang lại cho bản th&acirc;n.</p>', '5f1fc02cc0109tao-phong.png', 1),
(2, 'Tạo phồng', 99000, 0, '00:25:00', '<p><span style=\"color: #111111; font-family: HelveticaVn-LtCn; background-color: #ffffff;\">Nếu anh sở hữu một m&aacute;i t&oacute;c thưa, mỏng, uốn t&oacute;c sẽ gi&uacute;p m&aacute;i t&oacute;c trở n&ecirc;n bồng bềnh, tạo hiệu ứng tr&ocirc;ng d&agrave;y hơn. Ngược lại, những m&aacute;i t&oacute;c d&agrave;y, kh&ocirc; xơ sẽ trở n&ecirc;n mềm mại, nhẹ nh&agrave;ng hơn rất nhiều khi được uốn.</span></p>\r\n<p><span style=\"color: #111111; font-family: HelveticaVn-LtCn; background-color: #ffffff;\">&nbsp;</span></p>', '5f1fbfdaec70ftao-phong.png', 1),
(3, 'Uốn phồng cao cấp', 349000, 0, '00:35:00', '<p><span style=\"box-sizing: border-box; font-weight: bolder;\">G&oacute;i Uốn Phồng cao cấp ATS</span>&nbsp;của Poly-barber đem đến cho bạn những ưu điểm vượt trội:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1em; padding-left: 0px; list-style-type: none; color: #111111; font-family: HelveticaVn-LtCn; font-size: 24px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />Kh&ocirc;ng Amoniac g&acirc;y m&ugrave;i kh&oacute; chịu</li>\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />Kh&ocirc;ng MEA, kh&ocirc;ng HCL g&acirc;y hại t&oacute;c</li>\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />Tạo nếp t&oacute;c phồng ho&agrave;n hảo</li>\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />T&oacute;c giữ được sự mềm mượt tối đa</li>\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />C&oacute; thể uốn nhiều lần</li>\r\n<li style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 29px; margin: 0px 10px 5px 0px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/uon-phong/icon.png\" alt=\"Icon\" />D&ugrave;ng được cho t&oacute;c hư tổn</li>\r\n</ul>\r\n<p>Đặc biệt, sản phẩm được tăng cường th&agrave;nh phần Collagen v&agrave; Keratin đem lại độ su&ocirc;n mượt v&agrave; độ b&oacute;ng ho&agrave;n hảo cho t&oacute;c bạn.</p>', '5f1fc0d5a5edeuon_caocap.png', 1),
(4, 'Nhuộm Highlight', 70000, 0, '00:35:00', '<p><span style=\"color: #111111; font-family: HelveticaVn-LtCn; font-size: 18px; background-color: #ffffff;\">Nhuộm t&oacute;c đang trở th&agrave;nh xu hướng l&agrave;m đẹp cho nam giới. Nhuộm t&oacute;c kh&ocirc;ng những gi&uacute;p t&ocirc;n da mặt trở n&ecirc;n s&aacute;ng hơn, m&agrave; n&oacute; c&ograve;n l&agrave; c&aacute;ch để c&aacute;c ch&agrave;ng trai thể hiện phong c&aacute;ch, kh&iacute; chất ri&ecirc;ng của m&igrave;nh!</span></p>', '10.png', 3),
(5, 'Tẩy tóc', 100000, 0.1, '00:50:00', '<p style=\"text-align: left;\">Để sở hữu c&aacute;c m&agrave;u t&oacute;c nhuộm s&aacute;ng: kh&oacute;i, bạch kim, xanh,... bạn cần phải tẩy m&agrave;u t&oacute;c th&ecirc;m 1-2 lần để đam m&ecirc; của bạn th&agrave;nh hiện thực!</p>\r\n<p style=\"text-align: left;\">C&aacute;c m&agrave;u t&oacute;c s&aacute;ng: kh&oacute;i, bạch kim, xanh,... đang trở th&agrave;nh xu hướng của giới trẻ. Bạn sẽ phải tẩy m&agrave;u t&oacute;c th&ecirc;m 1-2 lần t&ugrave;y v&agrave;o độ s&aacute;ng của m&agrave;u t&oacute;c. Dịch vụ tẩy m&agrave;u t&oacute;c nam tại 30Shine sẽ gi&uacute;p bạn sở hữu m&agrave;u t&oacute;c ấn tượng m&agrave; bạn mong muốn!</p>\r\n<p style=\"text-align: left;\"><span style=\"color: #111111; font-family: HelveticaVn-LtCn; font-size: 20px; text-transform: uppercase; background-color: #ffffff;\">GI&Aacute; DỊCH VỤ: 100K/LẦN TẨY</span></p>\r\n<div class=\"title\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 5px; text-transform: uppercase; color: #111111; font-family: HelveticaVn-LtCn; font-size: 18px; background-color: #ffffff; text-align: left;\">QUY TR&Igrave;NH</div>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1em; color: #111111; font-family: HelveticaVn-LtCn; font-size: 18px; background-color: #ffffff; text-align: left;\">Để c&oacute; được m&agrave;u t&oacute;c s&aacute;ng, ph&ugrave; hợp với khu&ocirc;n mặt, trước khi nhuộm, c&aacute;c Stylist bậc cao gi&agrave;u kinh nghiệm của 30Shine sẽ tiến h&agrave;nh tẩy t&oacute;c từ 1-2 lần theo quy tr&igrave;nh chuy&ecirc;n nghiệp:</p>\r\n<div class=\"ant-row\" style=\"box-sizing: border-box; flex-flow: row wrap; display: flex; padding-bottom: 0px; background: #ffffff; color: #111111; font-family: HelveticaVn-LtCn; font-size: 18px; text-align: left;\">\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px;\">\r\n<div class=\"title\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 5px; text-transform: uppercase; text-align: left;\">1/ GỘI XẢ</div>\r\n<img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 482px; margin-bottom: 10px; float: left;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/tay-toc/Buoc-1-tay-toc-nam-30shine.png\" alt=\"Icon\" /></div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">&nbsp;</div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">T&oacute;c sẽ được gội qua 1 lần, kh&ocirc;ng g&atilde;i mạnh để khi v&agrave;o thuốc kh&ocirc;ng bị x&oacute;t.</div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">&nbsp;</div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">\r\n<div class=\"title\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 5px; text-transform: uppercase;\">2/ TẨY T&Oacute;C</div>\r\n<img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 482px; margin-bottom: 10px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/tay-toc/buoc-2-tay-toc-nam-30shine.png\" alt=\"Icon\" /></div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">&nbsp;</div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">Pha bột tẩy + Oxy 12, chải c&aacute;ch ch&acirc;n t&oacute;c 1-2cm. Ủ khoảng 30-40p tuỳ t&oacute;c cứng hay mềm.</div>\r\n</div>\r\n<div class=\"ant-row\" style=\"box-sizing: border-box; flex-flow: row wrap; display: flex; padding-bottom: 0px; background: #ffffff; color: #111111; font-family: HelveticaVn-LtCn; font-size: 18px; margin-bottom: 5px;\">\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">\r\n<div class=\"title\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 5px; text-transform: uppercase;\">3/ GỘI XẢ</div>\r\n<img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; width: 482px; margin-bottom: 10px;\" src=\"https://storage.30shine.com/ResourceWeb/data/images/Service/tay-toc/Buoc-3-tay-toc-nam-30shine.png\" alt=\"Icon\" /></div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">&nbsp;</div>\r\n<div class=\"ant-col ant-col-xs-24 ant-col-sm-24 ant-col-md-12 ant-col-lg-12\" style=\"box-sizing: border-box; position: relative; max-width: 50%; min-height: 1px; flex: 0 0 50%; padding: 10px 15px 15px; text-align: left;\">Xả lại bằng dầu xả để t&oacute;c mềm mượt hơn khi sấy v&agrave; tạo kiểu vuốt s&aacute;p</div>\r\n</div>', '5f223698e2694tay.png', 3),
