INSERT INTO `setting` (`id`, `logo`, `file_ico`, `title`, `introduce`, `slogan`) VALUES
(3, '64e421a31895elogox.png', 'favicon.ico', 'Poly-barber', '<p><strong style=\"box-sizing: border-box;\">I. VỀ POLY BARBER</strong></p>\r\n<p>Cuộc&nbsp;sống&nbsp;của&nbsp;ch&uacute;ng&nbsp;ta đang&nbsp;ng&agrave;y&nbsp;c&agrave;ng&nbsp;trở&nbsp;n&ecirc;n&nbsp;bận&nbsp;rộn<wbr style=\"box-sizing: border-box;\" />&nbsp;hơn&nbsp;với&nbsp;biết&nbsp;bao lo toan,&nbsp;suy nghĩ. Đặc biệt l&agrave; với nam giới, những người trụ cột trong gia đ&igrave;nh, đ&ocirc;i khi họ c&ograve;n kh&ocirc;ng c&oacute; thời gian nghĩ cho bản th&acirc;n, kh&ocirc;ng c&oacute; thời gian đi mua quần &aacute;o, ăn diện, hay thậm ch&iacute; l&agrave; đi cắt t&oacute;c cho gọn g&agrave;ng, bảnh bao hơn.</p>\r\n<p>Thấu hiểu được điều n&agrave;y, hệ thống t&oacute;c nam&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>&nbsp;đ&atilde; ra đời để đem đến cho c&aacute;nh m&agrave;y r&acirc;u một cuộc sống tiện lợi hơn trong việc chăm s&oacute;c đầu t&oacute;c. Đến với hệ thống t&oacute;c nam&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>, bạn sẽ kh&ocirc;ng phải lo lắng về vấn đề thời gian, chỉ cần 30 ph&uacute;t rảnh rỗi v&agrave;o bất cứ thời gian n&agrave;o,&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>&nbsp;sẽ mang đến cho bạn dịch vụ tận t&acirc;m, kỹ c&agrave;ng để bạn trở n&ecirc;n đẹp trai, s&aacute;ng l&aacute;ng ngay lập tức.</p>\r\n<p><strong style=\"box-sizing: border-box;\">II. SỨ MỆNH</strong></p>\r\n<p><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; display: block; margin-left: auto; margin-right: auto;\" src=\"https://i.imgur.com/XZQykPr.jpg\" /></p>\r\n<p>Hệ thống t&oacute;c nam&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>&nbsp;cung cấp dịch vụ l&agrave;m t&oacute;c chuy&ecirc;n nghiệp với gi&aacute; th&agrave;nh cạnh tranh, tiết kiệm thời gian v&agrave; l&agrave;m h&agrave;i l&ograve;ng tất cả đối tượng kh&aacute;ch h&agrave;ng nam giới bằng dịch vụ tốt nhất, mang đến sự thư gi&atilde;n kh&aacute;c biệt, n&acirc;ng cao gi&aacute; trị bản th&acirc;n với vẻ ngo&agrave;i trang nh&atilde;, tỏa s&aacute;ng.</p>\r\n<p>&nbsp;Ch&uacute;ng t&ocirc;i hạnh ph&uacute;c khi được phục vụ v&agrave; nhận được sự h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng.</p>\r\n<p><strong style=\"box-sizing: border-box;\">III. HỆ THỐNG CỬA H&Agrave;NG CHUY&Ecirc;N NGHIỆP</strong></p>\r\n<p><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; display: block; margin-left: auto; margin-right: auto;\" src=\"https://i.imgur.com/NoLJUVb.png\" /></p>\r\n<p>C&aacute;c cửa h&agrave;ng của&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>&nbsp;đều được đầu tư nghi&ecirc;m t&uacute;c, diện t&iacute;ch mặt bằng rộng r&atilde;i, tho&aacute;ng m&aacute;t trang thiết bị, nội thất đồng bộ, chuy&ecirc;n nghiệp, bắt mắt, đem đến trải nghiệm thoải m&aacute;i nhất cho kh&aacute;ch h&agrave;ng.</p>\r\n<p><strong style=\"box-sizing: border-box;\">IV.&nbsp;ĐỘI NGŨ THỢ STYLIST TẠO MẪU ĐẲNG CẤP</strong></p>\r\n<p><strong style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; display: block; margin-left: auto; margin-right: auto;\" src=\"https://i.imgur.com/tGfk5ts.png\" /></strong></p>\r\n<p>Đội ngũ thợ t&oacute;c của&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER</a>&nbsp;được tuyển dụng gắt gao, y&ecirc;u cầu cao về kinh nghiệm v&agrave; tay nghề để đảm bảo đem đến kết quả h&agrave;i l&ograve;ng nhất cho kh&aacute;ch h&agrave;ng. Đội ngũ&nbsp;<a style=\"box-sizing: border-box; background: 0px 0px; color: #302021; text-decoration-line: none;\" href=\"https://bbmen.vn/\">POLY BARBER </a>lu&ocirc;n t&igrave;m hiểu, học hỏi c&aacute;c xu hướng thời trang t&oacute;c tr&ecirc;n thế giới để lu&ocirc;n cập nhật nhanh ch&oacute;ng những mẫu t&oacute;c Hot nhất.</p>', 'Kinh nghiệm và Sáng tạo không ngừng cùng Poly Barber');

